import axios from "axios";

export const axiosWithAuth = () => {
  const token = localStorage.getItem("token"); //token覺 localStoragedan al覺r.
  return axios.create({
    baseURL: "http://localhost:9000/api/",
    headers: token ? { authorization: token } : {},
  });
};
